import React, { useState } from "react";

function App() {
  const [movies, setMovies] = useState([
    { id: 1, category: "Best Picture", title: "Movie A", votes: 0 },
    { id: 2, category: "Best Picture", title: "Movie B", votes: 0 },
    { id: 3, category: "Best Picture", title: "Movie C", votes: 0 },
    { id: 4, category: "Best Director", title: "Director A", votes: 0 },
    { id: 5, category: "Best Director", title: "Director B", votes: 0 },
    { id: 6, category: "Best Director", title: "Director C", votes: 0 },
    { id: 7, category: "Best Actor", title: "Actor A", votes: 0 },
    { id: 8, category: "Best Actor", title: "Actor B", votes: 0 },
    { id: 9, category: "Best Actor", title: "Actor C", votes: 0 },
    { id: 10, category: "Best Actress", title: "Actress A", votes: 0 },
    { id: 11, category: "Best Actress", title: "Actress B", votes: 0 },
    { id: 12, category: "Best Actress", title: "Actress C", votes: 0 },
  ]);

  const [voterName, setVoterName] = useState("");
  const [voterCategory, setVoterCategory] = useState("");
  const [voterVote, setVoterVote] = useState("");
  const [recentVotes, setRecentVotes] = useState([]);
  const [myVotes, setMyVotes] = useState([]);

  const handleSubmit = (event) => {
    event.preventDefault();
    let newMovies = [...movies];
    let votedMovie = newMovies.find(
      (movie) => movie.title === voterVote && movie.category === voterCategory
    );
    votedMovie.votes += 1;
    setMovies(newMovies);
    setRecentVotes([...recentVotes, { name: voterName, vote: voterVote }]);
    setMyVotes([...myVotes, { category: voterCategory, vote: voterVote }]);
  };

  return (
    <div className="App">
      <h1>Movie Awards Voting</h1>
      <div className="intro">
        <p>Welcome to the Movie Awards Voting App!</p>
      </div>
      <div className="stats">
        <h2>Statistics</h2>
        <p>Total Votes: {recentVotes.length}</p>
        <p>
          Best Picture:{" "}
          {movies
            .filter((movie) => movie.category === "Best Picture")
            .reduce((acc, movie) => acc + movie.votes, 0)}
        </p>
        <p>
          Best Director:{" "}
          {movies
            .filter((movie) => movie.category === "Best Director")
            .reduce((acc, movie) => acc + movie.votes, 0)}
        </p>
        <p>
          Best Actor:{" "}
          {movies
            .filter((movie) => movie.category === "Best Actor")
            .reduce((acc, movie) => acc + movie.votes, 0)}
        </p>
        <p>
          Best Actress:{" "}
          {movies
            .filter((movie) => movie.category === "Best Actress")
            .reduce((acc, movie) => acc + movie.votes, 0)}
        </p>
      </div>
      <div className="voting-section">
        <h2>Voting Section</h2>
        <div className="cards">
          {movies
            .filter((movie) => movie.category === "Best Picture")
            .map((movie) => (
              <div key={movie.id} className="card">
                <h3>{movie.category}</h3>
                <p>{movie.title}</p>
                <button
                  onClick={() => {
                    setVoterCategory("Best Picture");
                    setVoterVote(movie.title);
                  }}
                >
                  Vote
                </button>
                <button>Review</button>
              </div>
            ))}
          {movies
            .filter((movie) => movie.category === "Best Director")
            .map((movie) => (
