import React, { useState, useEffect } from "react";
import axios from "axios";
import useUserIdStore from '../store/User';


function MyReviews() {
    const { user_id } = useUserIdStore();


  const [review, setReview] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function fetchReview() {
      try {
        const response = await axios.get(`http://127.0.0.1:3000/show/${user_id}`);
        setReview(response.data.reviews);
        console.log(review)
      } catch (error) {
        console.error(error);
      } finally {
        setLoading(false);
      }
    }

    fetchReview();
  }, [user_id]);

  if (loading) {
    return <div className="Review-loading">Loading...</div>;
  }

  if (!review) {
    return <div className="Review-not-found">Review not found</div>;
  }

  return (
    <div className="Review">
      <h2 className="Review-title">{review.title}</h2>
      <p className="Review-body">{review.body}</p>
    </div>
  );
}

export default MyReview;
